import React, { useState } from 'react';
import { Link } from 'react-router-dom'

const Nav = () => {

const [basketSize, setBasketSize] = useState(0);

let itemsInBasket = null;

  return (
    <nav>
      <ul>
        <Link to="/">
          <li>Home</li>
        </Link>

        <p onClick={() => setBasketSize(basketSize + 2)}>Click me</p>

        <Link to="/basket">
        <li>Basket {basketSize}</li>
        </Link>

        <Link to="/contact">
          <li>Contact</li>
        </Link>

      </ul>
    </nav>
  )
}

export default Nav;
