import React, { Component } from 'react';
import { graphql } from 'react-apollo'; // To bind Apollo with React Component

/* Bootstrap */
// import { Container, Col, div className="", Button } from 'react-bootstrap';

/* Queries */
import { getProductsQuery } from '../queries/queries'

/* Component */
class ProductList extends Component {

  addProducts(product) {
    this.props.addProduct(product);
  }

  displayProducts() {
    let data = this.props.data;

    if (data.loading) {
      return (<p>Loading Products...</p>)
    } else {
      return data.products.map(product => {
        return (
          // <div className="Col-sm-12 Col-md-6 Col-lg-4 Col-xl-3 border">
          <div className="Col-sm-12 Col-md-6 Col-lg-4 Col-xl-3 border">
              <p key={product.id}>{product.name}</p>
              <p key={product.id}>{product.price}</p>
              <img key={product.id} src={product.img_url} alt="product" />
              <button onClick={() => this.addProducts(product)}>Add</button>
          </div>
        )
      })
    }
  }

  render() {
    console.log(this.props)
    return (
      <div className="container border" >
        <div className="row">
          {this.displayProducts()}
        </div>
      </div>
    )
  }
}



export default graphql(getProductsQuery)(ProductList); // query result accessible via props
