import React from 'react';

const Basket = (props) => {


  const displayBasket = () => {
    if (props.basket) {
      return props.basket.map(product => {
        return (
          <div className="col-12 basket border-bottom">
            <div className="product-img">
              <img className="img-fluid" key={product.id} src={product.img_url} alt="product" />
              <p className="text-center">remove</p>
            </div>
            <div className="name">
              <p key={product.id}>{product.name}</p>
            </div>
            <div className="price">
              <p key={product.id}>£{product.price.toFixed(2)}</p>
            </div>
          </div>
        )
      })
    }
  }

  const clearBasket = () => {
    props.setProductsInBasket([])
  }

  const getTotalPrice = () => {
    let total = 0;
    if (props.basket) {
      props.basket.map((product) => {
        return total += product.price;
      })
    }
    return <p>£{total.toFixed(2)}</p>;
  }

  return (
    <div>
      <h1 className="text-center pt-3 pb-3">Basket</h1>
      <div className="container">
        <div className="row">
          {displayBasket()}

          <button onClick={() => clearBasket()}>CLEAR BASKET</button>
          <div></div>
          <div></div>

          <p>Total Price:</p>
          {getTotalPrice()}
        </div>
          <button className="text-center">Checkout</button>
      </div>
    </div>
  )
}

export default Basket;
