import React, { useState, useEffect } from 'react';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo'; // binds apollo to React
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';


/* Component */
import ProductList from './components/ProductList';
import Nav from './components/Nav';
import Basket from './components/Basket';
import Contact from './components/Contact';
import Banner from './components/Banner';
import Footer from './components/Footer';

// Apollo Client Setup
const client = new ApolloClient({
  uri: "http://localhost:3001/graphql" //apollo knows we will be making requests to this end-point from our application
})

const App = () => {

  //State/Basket update issue

  let localData = null;

  // Storing the basket with local Storage
  const [productsInBasket, setProductsInBasket] = useState(() => {
    localData = localStorage.getItem("productsInBasket");
    return localData ? JSON.parse(localData) : [];
  });

  useEffect(() => {
    localStorage.setItem("productsInBasket", JSON.stringify(productsInBasket))
  }, [productsInBasket])

  // Add product to the basket
  const addProduct = (product) => {
    setProductsInBasket(productsInBasket.concat(product))
  }

  let index = null;
  // Remove product from basket
  const removeProduct = (productId) => {
    let basket = JSON.parse(localData);
    console.log(basket.length);

    // get index of item to delete by index
    // // Iterating through the basket to check if the ID selected is in, then get the index of that item in the basket
    basket.forEach(item => {
      if (item.id === productId) {
        index = basket.indexOf(item);
      }
    });

    console.log("index", index);

    basket.splice(index, 1);
    // // delete basket[]
    // // localStorage.removeItem()
    localStorage.setItem("productsInBasket", JSON.stringify(basket));
    setProductsInBasket(basket);
  }

  return (
    <Router>
      <ApolloProvider client={client}>

        <div className="App">

          <Nav basketSize={productsInBasket.length} />

          <Banner />

          <Switch>

            <Route
              path="/"
              render={() => <ProductList addProduct={addProduct} />}
              exact
            />

            <Route
              path="/basket"
              render={() => <Basket basket={productsInBasket} setProductsInBasket={setProductsInBasket} removeProduct={removeProduct} />}
              exact
            />

            <Route
              path="/contact"
              component={Contact}
              exact
            />

          </Switch>

          <Footer />

        </div>

      </ApolloProvider>
    </Router>

  );
}

export default App;
