import React from 'react';

const Basket = (props) => {

  const displayBasket = () => {
    if (props.basket) {
      console.log(props.basket)
      return props.basket.map(product => {
        return (
          <div>
            <p>{product.name}</p>
          </div>
        )
      })
    }
  }

  return (
    <div>
      <h1>Basket</h1>
      <p>size: {props.basket}</p>
      <p>{displayBasket()}</p>

    </div>
  )

}

export default Basket;
